INSERT INTO `tiny_goods` (`id`,`name`,`category_id`,`goods_no`,`pro_no`,`type_id`,`brand_id`,`unit`,`content`,`img`,`imgs`,`tag_ids`,`sell_price`,`market_price`,`cost_price`,`create_time`,`store_nums`,`warning_line`,`seo_title`,`seo_keywords`,`seo_description`,`weight`,`point`,`visit`,`favorite`,`sort`,`specs`,`attrs`,`prom_id`,`is_online`,`sale_protection`) VALUES ('6','【原封正品 移动4G公开版特价】Apple/苹果 iPhone 5S 苹果5S手机','2','AG0012320','AG0012320_1','1','2','件','<p>\r\n	</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div>\r\n	<p>\r\n		首款 iPhone 的问世，向人们展示了 \"触摸\" 这一人与智能手机极为自然的交互方式。欣赏音乐、浏览网页、拍摄照片，都在指尖轻触间即可完成。这一切皆源自极为简单的一点，那就是你的手指。今天，我们更顺其自然，将 Touch ID 指纹识别传感器带到你的指尖。你的指纹就是完美的密码，与你形影不离，而且根本没有人能够猜出它的样子。除此之外，我们认为你的手机应该认识你，记住你，而无需你记忆并输入密码才能使用它。我们还认为，那个你与 iPhone 自然而然频频接触的地方，就是放置传感器的绝佳位置：主屏幕按钮。但问题是，该如何在如此狭小的空间内集成传感器所需的全套技术元件呢？\r\n	</p>\r\n</div>\r\n<div>\r\n	<p>\r\n		这需要生物特征识别专家和硬件工程师来重新思考传感器技术的工作方式，并重新设计 iPhone 标志性的主屏幕按钮。按钮表面由激光切割的蓝宝石水晶制成，可将指纹图像传至电容式触摸传感器，该传感器会读取你的表层皮肤以获得清晰详尽的指纹。按钮周围是不锈钢环，用于监测你的手指，激活传感器和改善信噪比。随后，软件将读取你的指纹脊线，并查找匹配的指纹来为你解锁手机。于是在你的轻触之间，一整套高度先进的技术臻于无形。你甚至可能察觉不到它们的存在，但你一定会发现：解锁 iPhone 的方式一瞬间变得多么轻而易举。\r\n	</p>\r\n</div>\r\n<p>\r\n	</p>','data/uploads/2014/04/29/870c43499b99862429287c2d4119fef6.jpg','a:3:{i:0;s:60:\"data/uploads/2014/04/29/870c43499b99862429287c2d4119fef6.jpg\";i:1;s:60:\"data/uploads/2014/04/29/519ecfe92a47cf2b98d6504dd875c211.png\";i:2;s:60:\"data/uploads/2014/04/29/7a18d229142972a45f19d34dd1bbc3bb.jpg\";}','iphone','4898.00','5000.00','4700.00','2014-05-04 20:46:15','36','2','','','','300','20','0','0','1','a:2:{i:1;a:6:{s:2:\"id\";s:1:\"1\";s:4:\"name\";s:6:\"系统\";s:5:\"value\";a:1:{i:3;a:5:{s:2:\"id\";s:1:\"3\";s:7:\"spec_id\";s:1:\"1\";s:4:\"name\";s:18:\"苹果（IOS）16G\";s:3:\"img\";s:0:\"\";s:4:\"sort\";s:1:\"2\";}}s:4:\"type\";s:1:\"1\";s:4:\"note\";s:6:\"手机\";s:6:\"is_del\";s:1:\"0\";}i:2;a:6:{s:2:\"id\";s:1:\"2\";s:4:\"name\";s:6:\"颜色\";s:5:\"value\";a:3:{i:5;a:5:{s:2:\"id\";s:1:\"5\";s:7:\"spec_id\";s:1:\"2\";s:4:\"name\";s:6:\"白色\";s:3:\"img\";s:0:\"\";s:4:\